Ext.define('ContactosAlegra.controller.NuevoContactoController', {
    extend: 'Ext.app.Controller',

    refs: {
        RfBtCancelar: '#BtCancelar',
        RfBtGuardarOtro: '#BtGuardarOtro',
        RfBtGuardar: '#BtGuardar'
    },

    control: {
        "button": {
            click: 'onButtonClick'
        }
    },

    onButtonClick: function(button, e, eOpts) {
                if (button.itemId == "BtCancelar"){
                    Ext.Msg.confirm("Cancelar", "Â¿Esta seguro de Cancelar?", function(btnText){

                        if(btnText === "yes"){

                             Ext.getCmp('ViewNuevoContactoId').close();
                             Ext.getCmp('ViewContactosId').show();

                        }

                    }, this);

                }

                if (button.itemId == "BtGuardarOtro"){
                    Ext.Msg.alert('Status', 'hay que guardar y limpiar');

                }

                if (button.itemId == "BtGuardar"){
                     alert("hay que guardar y cerrar");
                     Ext.getCmp('ViewNuevoContactoId').close();
                     Ext.getCmp('ViewContactosId').show();

                }

    }

});
